column_creation &lt;- function(data, category) {
  category_col_name &lt;- as.character(substitute(category))
  mutate(data, 
    across(
      .cols = everything(), 
      .fns = \(x) ifelse(x, data[[category_col_name]], NA), 
      .names = "{col}_{category_col_name}"))
} 




